sourcesContent:
class Report {
  constructor() {
    this.reports = [];
  }

  /**
   *
   * @param {string} namespace
   * @param {string} url url where this happened
   * @param {string} text short text ( heading )
   * @param {string} description long text describing what it is about and how to fix it
   * @param {boolean} error
   * @param {number} importance 0 - lowest, 100 - highest
   */
  add(namespace, url, text, description, error = true, importance = 50) {
    this.reports.push({
      namespace: namespace,
      url: url,
      text: text,
      description: description,
      error: error,
      importance: importance,
    });
  }
  /**
   *
   * @returns {Array}
   */
  getReports() {
    return this.reports;
  }

  /**
   * Deletes all stored reports
   */
  clear() {
    this.reports.length = 0;
  }
}

export default new Report();

