import * as fetch from '../../functions/fetch.js';
import cookies from './cookies.js';

export default function(url) {
  return Promise.resolve(url)
    .then(fetch.fetch)
    .then((http) => {
      const result = {
        url: http.url,
        messages: [],
        errors: [],
      };
      try {
        cookies(http);
        result.messages.push('Cookies are set properly!');
      } catch(exception) {
        result.errors.push('Missing "secure; httpOnly" flags in cookie(s)!');
        result.errors.push('For more informations visit [see this page](https://github.com/juffalow/pentest-tool-lite/tree/master/src/security#cookies).');
      }
      return result;
    });
};

