import * as fetch from '../../functions/fetch.js';
import fingerprint from './fingerprint.js';

export default function(url) {
  return Promise.resolve(url)
    .then(fetch.fetch)
    .then((http) => {
      const result = {
        url: http.url,
        messages: [],
        errors: [],
      };
      try {
        fingerprint(http);
        result.messages.push('No fingerprint header is present!');
      } catch(exception) {
        result.errors.push(exception.message);
        result.errors.push('There is a header in the HTTP response that disclose the type of a running web server ( and its version ).');
        result.errors.push('For more informations visit [see this page](https://github.com/juffalow/pentest-tool-lite/tree/master/src/security#server-fingerprint).');
      }
      return result;
    });
};

