/**
 *
 * @param {Object} result
 * @returns {Array}
 */
export default function(result) {
  const error = [];
  if (result.response.headers.hasOwnProperty('x-powered-by')) {
    error.push('X-Powered-By');
  } else if (result.response.headers.hasOwnProperty('x-generator')) {
    error.push('X-Generator');
  } else if (result.response.headers.hasOwnProperty('server')) {
    error.push('Server');
  }
  if (error.length === 1) {
    throw new Error(error[0] + ' header is present!');
  } else if (error.length > 1) {
    throw new Error(error.join(', ') + 'headers are present!');
  }
};

