import * as fetch from '../../../functions/fetch.js';
import parseHtml from '../../../functions/parseHtml.js';
import getResources from '../../../functions/getResources.js';
import onlyJsFiles from '../onlyJsFiles.js';
import onlyAvailable from '../available';
import checkMinifiedJs from './minify.js';

export default function(url) {
  return Promise.resolve(url)
    .then(fetch.fetch)
    .then(parseHtml)
    .then(getResources)
    .then(onlyJsFiles)
    .then(onlyAvailable)
    .then((urls) => {
      return Promise.all(urls.map((singleUrl) => {
        return Promise.resolve(singleUrl)
          .then(fetch.fetch)
          .then((http) => {
            const result = {
              url: http.url,
              messages: [],
              errors: [],
            };
            try {
              checkMinifiedJs(http);
              result.messages.push('JavaScript is minified and uglified!');
            } catch(exception) {
              result.errors.push(exception.message);
            }
            return result;
          });
      }));
    });
};

